mapper.select:
SELECT file_key
FROM board
WHERE board_idx = #{board_idx}

SELECT ori_filename
FROM file
WHERE new_filename = #{filename}

SELECT b.board_idx, b.board_empl_idx, b.board_type_idx, b.dept_idx, b.board_title, b.board_content, b.board_view_cnt, DATE_FORMAT(b.board_write_date, '%Y-%m-%d %H:%i') AS board_write_date, DATE_FORMAT(b.board_update_date, '%Y-%m-%d %H:%i') AS board_update_date, e.empl_name, e.empl_id, d.dept_name, bt.board_type_name FROM board b JOIN employee e ON b.board_empl_idx = e.empl_idx JOIN department d ON b.dept_idx = d.dept_idx JOIN board_type bt ON b.board_type_idx = bt.board_type_idx WHERE 1 = 1
AND (b.board_type_idx = 3 OR b.board_type_idx = 1 OR (b.board_type_idx = 2 AND b.dept_idx = #{userDept}))
AND (b.board_type_idx != 2 AND b.dept_idx = #{dept} OR (b.board_type_idx = 2 AND b.dept_idx = #{userDept}))
AND bt.board_type_idx = #{type}
AND b.board_type_idx = 2 AND b.dept_idx = #{userDept}
AND b.dept_idx = #{dept} AND bt.board_type_idx = #{type}
AND b.board_type_idx = 2 AND b.dept_idx = #{userDept}
AND ${searchType} LIKE CONCAT('%', #{keyword}, '%') ORDER BY b.board_write_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT CEIL(COUNT(*) / COALESCE(#{cnt}, 15)) AS pages
FROM board b JOIN employee e ON b.board_empl_idx = e.empl_idx JOIN department d ON b.dept_idx = d.dept_idx JOIN board_type bt ON b.board_type_idx = bt.board_type_idx WHERE 1 = 1
AND (b.board_type_idx = 3 OR b.board_type_idx = 1 OR (b.board_type_idx = 2 AND b.dept_idx = #{userDept}))
AND b.dept_idx = #{dept}
AND bt.board_type_idx = #{type}
AND b.board_type_idx = 2 AND b.dept_idx = #{userDept}
AND b.dept_idx = #{dept} AND bt.board_type_idx = #{type}
AND ${searchType} LIKE CONCAT('%', #{keyword}, '%')

mapper.update:
UPDATE reply 
SET reply = #{reply}
WHERE reply_idx = #{reply_idx}
